import React, { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import { signIn } from '../actions/authActions';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../css/Task.css';

function TaskScreen() {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const [dropdownVisible, setDropdownVisible] = useState(false);
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [selectedUser, setSelectedUser] = useState<any>(null);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [dueDate, setDueDate] = useState('');


    useEffect(() => {

        const getUsers = async () => {
            try {
                const response = await axios.get('http://localhost:5001/users');
                const data = response.data;
                setUsers(data)

            } catch (error) {
                console.error('Something went wrong, please try again');
            }
        };

        getUsers();
    }, []);

    const handleButtonClick = () => {
        setDropdownVisible((prev) => !prev);
    };

    const handleUserSelect = (user: any) => {
        setSelectedUser(user);
        setDropdownVisible(false); // Hide dropdown after selecting a user
    };

    const handleAddTask = () => {
        if (!selectedUser || !title || !description || !dueDate) {
            alert('Please fill in all fields.');
            return;
        }
        const newTask = {
            userId: selectedUser.id,
            title,
            description,
            dueDate,
        };
        console.log('New Task:', newTask); // Replace this with actual task submission logic
    };


    return (
        <div>
            <button onClick={handleButtonClick}>Add Task</button>
            {dropdownVisible && (
                <div className="dropdown">
                    {loading ? (
                        <p>Loading users...</p>
                    ) : error ? (
                        <p>{error}</p>
                    ) : (
                        <ul>
                            {users.map((user: any) => (
                                <li key={user.id}>{user.email}</li>
                            ))}
                        </ul>
                    )}
                </div>
            )}

        </div>
    );
}

export default TaskScreen;
