import React, { useEffect } from 'react';
import { Provider, useDispatch, useSelector } from 'react-redux';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { restoreToken, signIn, signOut } from './actions/authActions';
import Example from './screens/example';

import HomeScreen from './screens/HomeScreen';
import SignInScreen from './screens/SignInScreen';
import store from './store';

function App() {
  const dispatch = useDispatch();
  const { isLoading, userToken } = useSelector((state: any) => state.auth);

  // UseEffect to listen for changes in userToken
  useEffect(() => {
    if (userToken) {
      // This code runs whenever userToken changes (when signIn is triggered)
      console.log('User has signed in with token:', userToken);

      // Call any function you need here, for example:
      someFunctionAfterSignIn();

      // Example: Redirect to home page after sign-in
      // navigate('/home');
    }
  }, [userToken]); // Dependency on userToken, it runs when it changes

  // Example function you want to call when signed in
  const someFunctionAfterSignIn = () => {
    // Any logic you want to execute after sign-in
    console.log('Executing some post-sign-in logic...');
  };

  useEffect(() => {
    // Fetch the token from localStorage
    const bootstrapAsync = async () => {
      let userToken: any;

      try {
        userToken = localStorage.getItem('userToken');
        console.log('hyri');

      } catch (e) {
        console.log('hyri error');

        // Error restoring token
      }
      console.log('cbehet');

      dispatch(restoreToken(userToken));
    };

    bootstrapAsync();
  }, [dispatch]);

  const isSignedIn = userToken != null;

  if (isLoading) {
    // Show loading state while the token is being restored
    return <div>Loading...</div>;
  }

  return (
    <Router>
      <Routes>
        {!isSignedIn ? (
          // Authenticated user flow
          <>
            <Route path="/" element={<HomeScreen />} />

          </>
        ) : (
          // Unauthenticated user flow
          <>
            <Route path="/" element={<SignInScreen />} />
            <Route path="/example" element={<Example />} />

          </>
        )}
      </Routes>
    </Router>
  );
}

// Wrap your app in the Provider component and pass the store
export default function AppWithProvider() {
  return (
    <Provider store={store}>
      <App />
    </Provider>
  );
}
