import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { signIn } from '../actions/authActions';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './SignInScreen.css';  // Import external CSS file for styling

function SignInScreen() {
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSignIn = async () => {
        try {
            const response = await axios.post('http://localhost:5001/signin', {
                email: username,
                password: password,
            });

            const data = response.data;
            console.log('response', data.accessToken);

            localStorage.setItem('userToken', data.accessToken);
            dispatch(signIn(data.accessToken));

        } catch (error) {
            setError('Something went wrong, please try again');
        }
    };

    return (
        <div className="sign-in-container">
            <h1>Sign In</h1>
            <div className="input-group">
                <input
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    placeholder="Username"
                    className="input-field"
                />
                <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Password"
                    className="input-field"
                />
            </div>
            <button onClick={handleSignIn} className="sign-in-button">Sign In</button>
            {error && <div className="error-message">{error}</div>}
        </div>
    );
}

export default SignInScreen;
