import React from 'react';
import { Line } from 'react-chartjs-2';
import { useDispatch } from 'react-redux';
import { signOut } from '../actions/authActions';
import '../css/HomeScreen.css';

import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
} from 'chart.js';


ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
);

const dataset1Data = [120, -200, 300, -450, 600, -800, 900];

export const options = {
    responsive: true,
    elements: {
        line: {
            tension: 0.5,
        },
    },
    plugins: {
        legend: {
            position: 'top' as const,
        },
        title: {
            display: true,
            text: 'Tasks Completed This Month',
            color: 'black',
            font: { wieght: 'bold', size: 22 },
            align: 'center'
        },
    },
};

const labels = ['January', 'February', 'March', 'April', 'May', 'June', 'July'];

export const data = {
    labels,
    datasets: [
        {
            label: 'Dataset 1',
            data: dataset1Data,  // Use real or precomputed data here
            borderColor: 'rgb(255, 99, 132)',
            backgroundColor: 'rgba(255, 99, 132, 0.5)',
        },
    ],
};

function HomeScreen() {
    const dispatch = useDispatch();

    const handleSignOut = () => {
        dispatch(signOut());
        localStorage.removeItem('userToken');
    };



    return (
        <div className="dashboard-container">
            <div className="dashboard-header">
                <h1>Welcome to the Dashboard</h1>
                <button onClick={handleSignOut}>Sign out</button>
            </div>

            <div className="card-container">
                <div className="card">Card 1</div>
                <div className="card">Card 2</div>
                <div className="card">Card 3</div>
                <div className="card">Card 4</div>
            </div>

            <div className="content-section">
                <div className="text-section">
                    <p>Some description or information goes here.</p>
                </div>
                <div className="chart-section">
                    <Line options={options} data={data} />
                </div>
            </div>

            <div className="list-section">
                <div className="main-list">
                    <p>Main list of items</p>
                </div>
                <div className="side-list">
                    <p>Smaller side list</p>
                </div>
            </div>
        </div>
    );
}

export default HomeScreen;
