import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { signIn } from '../actions/authActions';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../css/SignInScreen.css';
import welcomeImage from '../images/background-login.jpg';

function SignInScreen() {
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSignIn = async () => {
        try {
            const response = await axios.post('http://localhost:5001/signin', {
                email: username,
                password: password,
            });

            const data = response.data;
            console.log('response', data);

            localStorage.setItem('userToken', data.accessToken);
            localStorage.setItem('userID', data.id);

            const userResponse = await axios.get(`http://localhost:5001/users/${data.id}`, {
                headers: {
                    Authorization: `Bearer ${data.accessToken}`
                }
            }); console.log('userResponse', userResponse);


            dispatch(signIn(data.accessToken));
        } catch (error) {
            setError('Something went wrong, please try again');
        }
    };

    return (
        <div className="sign-in-page">
            <div className="image-container">
                <img
                    src={welcomeImage}
                    alt="Welcome"
                    className="welcome-image"
                />
            </div>
            <div className="sign-in-container">
                <h1>Sign In</h1>
                <div className="input-group">
                    <input
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        placeholder="Username"
                        className="input-field"
                    />
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Password"
                        className="input-field"
                    />
                </div>
                <button onClick={handleSignIn} className="sign-in-button">Sign In</button>
                {error && <div className="error-message">{error}</div>}
            </div>
        </div>
    );
}

export default SignInScreen;
