import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import { useDispatch } from 'react-redux';
import { signOut } from '../actions/authActions';
import '../css/HomeScreen.css';

import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
} from 'chart.js';
import axios from 'axios';

ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
);

function HomeScreen() {
    const dispatch = useDispatch();
    const [totalTask, setTotalTask] = useState(0);
    const [totalTaskInProgress, setTotalTaskInProgress] = useState(0);
    const [totalTaskReview, setTotalTaskReview] = useState(0);
    const [totalTaskCompleted, setTotalTaskCompleted] = useState(0);
    const [completedTasksByMonth, setCompletedTasksByMonth] = useState<number[]>([]);
    const [selectedTimeRange, setSelectedTimeRange] = useState('lastYear'); // Default: last year

    const options = {
        responsive: true,
        elements: {
            line: {
                tension: 0.5,
            },
        },
        plugins: {
            legend: {
                position: 'top' as const,
            },
            title: {
                display: true,
                text: 'Tasks Completed',
                color: 'black',
                font: { weight: 'bold', size: 22 },
            },
        },
        scales: {
            y: {
                suggestedMax: Math.max(...completedTasksByMonth) + 2,
                beginAtZero: true,
            },
        },
    };

    const labels = [
        'January', 'February', 'March', 'April', 'May', 'June', 'July', 'August',
        'September', 'October', 'November', 'December'
    ];

    const data = {
        labels,
        datasets: [
            {
                label: 'Completed Tasks',
                data: completedTasksByMonth,
                borderColor: 'rgb(75, 192, 192)',
                backgroundColor: 'rgba(75, 192, 192, 0.2)',
            },
        ],
    };

    useEffect(() => {
        const handleTaskCall = async () => {
            try {
                const response = await axios.get('http://localhost:5001/tasks');
                const data = response.data;

                const completedTasks = data.filter((task: any) => task.status === 'completed');
                setTotalTaskInProgress(data.filter((task: any) => task.status === 'in-progress').length);
                setTotalTaskReview(data.filter((task: any) => task.status === 'review').length);
                setTotalTaskCompleted(completedTasks.length);
                setTotalTask(data.length);

                // Extract the completed tasks by month
                const completedTasksMonthCount = new Array(12).fill(0);
                completedTasks.forEach((task: any) => {
                    const finishedDate = new Date(task.finishedDate);
                    const month = finishedDate.getMonth(); // Get month (0-11)
                    completedTasksMonthCount[month]++;
                });

                // Update the data based on selected time range
                filterDataByTimeRange(completedTasksMonthCount);

            } catch (error) {
                console.error('Something went wrong, please try again');
            }
        };

        handleTaskCall();
    }, [selectedTimeRange]); // Re-run when selected time range changes

    const filterDataByTimeRange = (completedTasksMonthCount: number[]) => {
        const currentDate = new Date();
        let filteredData = [...completedTasksMonthCount];

        switch (selectedTimeRange) {
            case 'lastYear':
                break; // No filtering needed for the last year
            case 'last6Months':
                filteredData = completedTasksMonthCount.slice(-6); // Get last 6 months
                break;
            case 'last3Months':
                filteredData = completedTasksMonthCount.slice(-3); // Get last 3 months
                break;
            case 'lastMonth':
                filteredData = completedTasksMonthCount.slice(-1); // Get the last month
                break;
            case 'lastWeek':
                // Assuming we want to filter the last week (you can adjust as needed)
                filteredData = [0]; // For simplicity, replace with last week's logic
                break;
            default:
                break;
        }

        setCompletedTasksByMonth(filteredData);
    };

    const handleSignOut = () => {
        dispatch(signOut());
        localStorage.removeItem('userToken');
    };

    const handleTimeRangeChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
        setSelectedTimeRange(e.target.value);
    };

    const AnimatedNumber = ({ targetNumber }: any) => {
        const [count, setCount] = useState(0);

        React.useEffect(() => {
            const interval = setInterval(() => {
                setCount(prevCount => {
                    if (prevCount < targetNumber) {
                        return prevCount + 1;
                    } else {
                        clearInterval(interval);
                        return targetNumber;
                    }
                });
            }, 5);

            return () => clearInterval(interval);
        }, [targetNumber]);

        return <div className="card-number">{count}</div>;
    };

    return (
        <div className="dashboard-container">
            <div className="dashboard-header">
                <h1>Welcome to the Dashboard</h1>
                <button onClick={handleSignOut}>Sign out</button>
            </div>

            <div className="card-container">
                <div className="card">
                    <div className="card-title">Total Tasks</div>
                    <AnimatedNumber targetNumber={totalTask} />
                </div>
                <div className="card">
                    <div className="card-title">Tasks In Progress</div>
                    <AnimatedNumber targetNumber={totalTaskInProgress} />
                </div>
                <div className="card">
                    <div className="card-title">Tasks Under Review</div>
                    <AnimatedNumber targetNumber={totalTaskReview} />
                </div>
                <div className="card">
                    <div className="card-title">Completed Tasks</div>
                    <AnimatedNumber targetNumber={totalTaskCompleted} />
                </div>
            </div>

            <div className="content-section">
                <div className="text-section">
                    <p>Some description or information goes here.</p>
                </div>
                <div className="chart-section">
                    <select value={selectedTimeRange} onChange={handleTimeRangeChange}>
                        <option value="lastYear">Last Year</option>
                        <option value="last6Months">Last 6 Months</option>
                        <option value="last3Months">Last 3 Months</option>
                        <option value="lastMonth">Last Month</option>
                        <option value="lastWeek">Last Week</option>
                    </select>
                    <Line options={options} data={data} />
                </div>
            </div>

            <div className="list-section">
                <div className="main-list">
                    <p>Main list of items</p>
                </div>
                <div className="side-list">
                    <p>Smaller side list</p>
                </div>
            </div>
        </div>
    );
}

export default HomeScreen;
