import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import { useDispatch } from 'react-redux';
import { signOut } from '../actions/authActions';
import '../css/HomeScreen.css';

import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
} from 'chart.js';
import axios from 'axios';

ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
);

export const options = {
    // responsive: true,
    elements: {
        line: {
            tension: 0.5,
        },
    },
    plugins: {
        legend: {
            position: 'top' as const,
        },
        title: {
            display: true,
            text: 'Tasks Completed',
            color: 'black',
            font: { weight: 'bold', size: 22 },
        },
    },
};

const labels = ['January', 'February', 'March', 'April', 'May', 'June', 'July'];

function HomeScreen() {
    const dispatch = useDispatch();
    const [totalTask, setTotalTask] = useState(0);
    const [totalTaskInProgress, setTotalTaskInProgress] = useState(0);
    const [totalTaskReview, setTotalTaskReview] = useState(0);
    const [totalTaskCompleted, setTotalTaskCompleted] = useState(0);
    const [tasks, setTasks] = useState([]);
    const [selectedPeriod, setSelectedPeriod] = useState('1m'); // Default to 1 month

    const filterTasksByPeriod = (tasks: any[], period: string) => {
        const now = new Date();
        let filteredTasks = tasks;

        switch (period) {
            case '1w': // This week
                filteredTasks = tasks.filter(task => {
                    const taskDate = new Date(task.finishedDate);
                    return taskDate >= new Date(now.setDate(now.getDate() - now.getDay()));
                });
                break;
            case '1m': // Last 1 month
                filteredTasks = tasks.filter(task => {
                    const taskDate = new Date(task.finishedDate);
                    return taskDate >= new Date(now.setMonth(now.getMonth() - 1));
                });
                break;
            case '3m': // Last 3 months
                filteredTasks = tasks.filter(task => {
                    const taskDate = new Date(task.finishedDate);
                    return taskDate >= new Date(now.setMonth(now.getMonth() - 3));
                });
                break;
            case '6m': // Last 6 months
                filteredTasks = tasks.filter(task => {
                    const taskDate = new Date(task.finishedDate);
                    return taskDate >= new Date(now.setMonth(now.getMonth() - 6));
                });
                break;
            case '1y': // Last 1 year
                filteredTasks = tasks.filter(task => {
                    const taskDate = new Date(task.finishedDate);
                    return taskDate >= new Date(now.setFullYear(now.getFullYear() - 1));
                });
                break;
            default:
                break;
        }

        return filteredTasks;
    };

    useEffect(() => {
        const handleTaskCall = async () => {
            try {
                const response = await axios.get('http://localhost:5001/tasks');
                const data = response.data;
                console.log('response', data);
                setTasks(data);
                const filteredTasks = filterTasksByPeriod(data, selectedPeriod);

                const inProgressTasks = filteredTasks.filter((task: any) => task.status === 'in-progress');
                const inReviewTasks = filteredTasks.filter((task: any) => task.status === 'review');
                const inCompletedTasks = filteredTasks.filter((task: any) => task.status === 'completed');

                setTotalTaskInProgress(inProgressTasks.length);
                setTotalTaskReview(inReviewTasks.length);
                setTotalTaskCompleted(inCompletedTasks.length);
                setTotalTask(filteredTasks.length);
            } catch (error) {
                console.error('Something went wrong, please try again');
            }
        };

        handleTaskCall();
    }, [selectedPeriod]); // Re-fetch when the selected period changes

    const handleSignOut = () => {
        dispatch(signOut());
        localStorage.removeItem('userToken');
    };

    const AnimatedNumber = ({ targetNumber }: any) => {
        const [count, setCount] = useState(0);

        React.useEffect(() => {
            const interval = setInterval(() => {
                setCount(prevCount => {
                    if (prevCount < targetNumber) {
                        return prevCount + 1;
                    } else {
                        clearInterval(interval);
                        return targetNumber;
                    }
                });
            }, 5);

            return () => clearInterval(interval);
        }, [targetNumber]);

        return <div className="card-number">{count}</div>;
    };

    const handlePeriodChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
        setSelectedPeriod(e.target.value);
    };

    return (
        <div className="dashboard-container">
            <div className="dashboard-header">
                <h1>Welcome to the Dashboard</h1>
                <button onClick={handleSignOut}>Sign out</button>
            </div>

            <div className="period-selector">
                <select onChange={handlePeriodChange} value={selectedPeriod}>
                    <option value="1y">Last 1 Year</option>
                    <option value="6m">Last 6 Months</option>
                    <option value="3m">Last 3 Months</option>
                    <option value="1m">Last 1 Month</option>
                    <option value="1w">This Week</option>
                </select>
            </div>

            <div className="card-container">
                <div className="card">
                    <div className="card-title">Total Tasks</div>
                    <AnimatedNumber targetNumber={totalTask} />
                </div>
                <div className="card">
                    <div className="card-title">Tasks In Progress</div>
                    <AnimatedNumber targetNumber={totalTaskInProgress} />
                </div>
                <div className="card">
                    <div className="card-title">Tasks Under Review</div>
                    <AnimatedNumber targetNumber={totalTaskReview} />
                </div>
                <div className="card">
                    <div className="card-title">Completed Tasks</div>
                    <AnimatedNumber targetNumber={totalTaskCompleted} />
                </div>
            </div>

            <div className="content-section">
                <div className="text-section">
                    <p>Some description or information goes here.</p>
                </div>
                <div className="chart-section">
                    <Line options={options} data={{ labels, datasets: [{ label: 'Done Tasks', data: [totalTaskInProgress, totalTaskReview, totalTaskCompleted], borderColor: 'rgb(255, 99, 132)', backgroundColor: 'rgba(255, 99, 132, 0.5)' }] }} />
                </div>
            </div>
        </div>
    );
}

export default HomeScreen;
